html:
{% set grouped_items = {} %}
{% for item in doc.items %}

    {% set work_type = item.custom_work_type %}
    {% if not grouped_items.get(work_type) %}
        {% set _ = grouped_items.update({ work_type: [] }) %}
    {% endif %}
    {% set _ = grouped_items[work_type].append(item) %}
{% endfor %}

{% for work_type, items in grouped_items.items() %}
    <h4>{{ work_type }}</h4> 
    <table class="table table-bordered" style="border: 2px solid black; text-align: center;">
        <thead>
            <tr>
                <th style="border: 2px solid black; text-align: center;" rowspan="2">Seq</th>
                <th style="border: 2px solid black; text-align: center;" rowspan="2">Vessels</th>
                <th style="border: 2px solid black; text-align: center;" colspan="{{ items | map(attribute='custom_location') | unique | list | length }}">Daily Rate / Location</th>
            </tr>
            <tr>
                {% for location in items | map(attribute='custom_location') | unique %}
                <th style="border: 2px solid black; text-align: center;">{{ location }}</th>
                {% endfor %}
            </tr>
        </thead>
        <tbody>
            {% for item in items %}
            <tr>
                <td style="border: 2px solid black; text-align: center;">{{ loop.index }}</td>
                <td style="border: 2px solid black; text-align: center;">{{ item.custom_vessels }}</td>
                {% for location in items | map(attribute='custom_location') | unique %}
                <td style="border: 2px solid black; text-align: center;">
                    {% if item.custom_location == location %}
                        {{ item.rate }}
                    {% else %}
                        -
                    {% endif %}
                </td>
                {% endfor %}
            </tr>
            {% endfor %}
        </tbody>
    </table>
{% endfor %}
